// ============================================================
// ITEMLIST.JS - Component to Display All Inventory Items
// ============================================================
// This component shows all items in two formats:
//   - Cards (for mobile devices)
//   - Table (for larger screens)
// CSS handles which format is shown based on screen size.
// ============================================================

import React from 'react';

// Props (inputs) this component receives:
// - items: array of item objects to display
// - onDelete: function to call when delete button is clicked
// - onEdit: function to call when edit button is clicked
// - onUpdateQuantity: function to call when +/- buttons are clicked
function ItemList({ items, onDelete, onEdit, onUpdateQuantity }) {
  // ========== SHOW MESSAGE IF NO ITEMS ==========
  if (!items || items.length === 0) {
    return (
      <div className="no-items">
        <p>üì≠ No items in inventory</p>
        <p>Add your first item using the form above!</p>
      </div>
    );
  }

  // ========== HELPER FUNCTION TO FORMAT CURRENCY ==========
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  // ========== RENDER ITEMS ==========
  return (
    <div className="item-list">
      {/* ========== MOBILE VIEW: CARDS ========== */}
      {/* This is shown on small screens */}
      {items.map((item) => (
        <div key={item.id} className="item-card">
          {/* Card Header with Name */}
          <div className="item-card-header">
            <span className="item-name">{item.name}</span>
          </div>

          {/* Card Body with Details */}
          <div className="item-card-body">
            {/* Price */}
            <div className="item-detail">
              <span className="item-detail-label">Price</span>
              <span className="item-detail-value price">
                {formatCurrency(item.price)}
              </span>
            </div>

            {/* Quantity with +/- Controls */}
            <div className="item-detail">
              <span className="item-detail-label">Quantity</span>
              <div className="quantity-controls">
                {/* Decrease Button */}
                <button
                  className="quantity-btn"
                  onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}
                  disabled={item.quantity <= 0}
                  title="Decrease quantity"
                >
                  -
                </button>
                {/* Current Quantity */}
                <span className="quantity-value">{item.quantity}</span>
                {/* Increase Button */}
                <button
                  className="quantity-btn"
                  onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}
                  title="Increase quantity"
                >
                  +
                </button>
              </div>
            </div>

            {/* Total Value */}
            <div className="item-detail">
              <span className="item-detail-label">Total</span>
              <span className="item-detail-value total">
                {formatCurrency(item.quantity * item.price)}
              </span>
            </div>
          </div>

          {/* Card Actions (Edit/Delete) */}
          <div className="item-actions">
            <button
              className="btn btn-secondary btn-small"
              onClick={() => onEdit(item)}
            >
              ‚úèÔ∏è Edit
            </button>
            <button
              className="btn btn-danger btn-small"
              onClick={() => onDelete(item.id)}
            >
              üóëÔ∏è Delete
            </button>
          </div>
        </div>
      ))}

      {/* ========== DESKTOP VIEW: TABLE ========== */}
      {/* This is shown on larger screens (controlled by CSS) */}
      <table className="item-table">
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total Value</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              {/* Item Name */}
              <td>
                <strong>{item.name}</strong>
              </td>

              {/* Price */}
              <td className="price">
                {formatCurrency(item.price)}
              </td>

              {/* Quantity with Controls */}
              <td>
                <div className="quantity-controls">
                  <button
                    className="quantity-btn"
                    onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}
                    disabled={item.quantity <= 0}
                    title="Decrease quantity"
                  >
                    -
                  </button>
                  <span className="quantity-value">{item.quantity}</span>
                  <button
                    className="quantity-btn"
                    onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}
                    title="Increase quantity"
                  >
                    +
                  </button>
                </div>
              </td>

              {/* Total Value */}
              <td className="total">
                <strong>{formatCurrency(item.quantity * item.price)}</strong>
              </td>

              {/* Actions */}
              <td>
                <div className="item-actions">
                  <button
                    className="btn btn-secondary btn-small"
                    onClick={() => onEdit(item)}
                  >
                    ‚úèÔ∏è Edit
                  </button>
                  <button
                    className="btn btn-danger btn-small"
                    onClick={() => onDelete(item.id)}
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItemList;
