// ============================================================
// STATS.JS - Statistics Display Component
// ============================================================
// This component shows a summary of the inventory:
//   - Total number of different items
//   - Total quantity of all items combined
//   - Total value of the entire inventory
// ============================================================

import React from 'react';

// Props (inputs) this component receives:
// - stats: object containing { totalItems, totalQuantity, totalValue }
function Stats({ stats }) {
  // ========== HELPER FUNCTION TO FORMAT CURRENCY ==========
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  // ========== RENDER THE STATS PANEL ==========
  return (
    <section className="stats-section">
      <h2>ðŸ“Š Inventory Summary</h2>
      <div className="stats-panel">
        {/* Total Items Card */}
        <div className="stat-card">
          <div className="stat-value">{stats.totalItems}</div>
          <div className="stat-label">Total Items</div>
        </div>

        {/* Total Quantity Card */}
        <div className="stat-card">
          <div className="stat-value">{stats.totalQuantity}</div>
          <div className="stat-label">Total Units</div>
        </div>

        {/* Total Value Card (highlighted) */}
        <div className="stat-card highlight">
          <div className="stat-value">{formatCurrency(stats.totalValue)}</div>
          <div className="stat-label">Total Value</div>
        </div>
      </div>
    </section>
  );
}

export default Stats;
