// ============================================================
// APP.JS - Main React Component
// ============================================================
// This is the main component of our application.
// It contains all the logic for managing inventory items.
// ============================================================

// Import React and hooks (useState, useEffect)
// - useState: lets us store data that can change
// - useEffect: lets us run code when the component loads
import React, { useState, useEffect } from 'react';

// Import our CSS styles
import './App.css';

// Import our child components
import ItemForm from './components/ItemForm';
import ItemList from './components/ItemList';
import SearchBar from './components/SearchBar';
import Stats from './components/Stats';

// ============================================================
// API URL CONFIGURATION
// ============================================================
// In development, we use localhost
// In production (deployed), we use the actual server URL
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// ============================================================
// MAIN APP COMPONENT
// ============================================================
function App() {
  // ========== STATE VARIABLES ==========
  // These store data that can change over time
  
  // items: array of all inventory items
  const [items, setItems] = useState([]);
  
  // stats: inventory statistics (total value, etc.)
  const [stats, setStats] = useState({ totalItems: 0, totalQuantity: 0, totalValue: 0 });
  
  // searchTerm: what the user is searching for
  const [searchTerm, setSearchTerm] = useState('');
  
  // loading: whether we're loading data from the server
  const [loading, setLoading] = useState(true);
  
  // error: any error message to display
  const [error, setError] = useState('');
  
  // editingItem: the item currently being edited (null if not editing)
  const [editingItem, setEditingItem] = useState(null);

  // ========== LOAD DATA WHEN APP STARTS ==========
  // useEffect runs code when the component first loads
  // The empty [] means it only runs once when the app starts
  useEffect(() => {
    fetchItems();
    fetchStats();
  }, []);

  // ========== FETCH ALL ITEMS FROM SERVER ==========
  const fetchItems = async (search = '') => {
    try {
      setLoading(true);
      setError('');
      
      // Build the URL with search parameter if provided
      const url = search 
        ? `${API_URL}/items?search=${encodeURIComponent(search)}`
        : `${API_URL}/items`;
      
      // Make the API request
      const response = await fetch(url);
      const data = await response.json();
      
      if (data.success) {
        setItems(data.data);
      } else {
        setError(data.error || 'Failed to load items');
      }
    } catch (err) {
      console.error('Error fetching items:', err);
      setError('Could not connect to server. Is the backend running?');
    } finally {
      setLoading(false);
    }
  };

  // ========== FETCH INVENTORY STATISTICS ==========
  const fetchStats = async () => {
    try {
      const response = await fetch(`${API_URL}/stats`);
      const data = await response.json();
      
      if (data.success) {
        setStats(data.data);
      }
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  // ========== ADD A NEW ITEM ==========
  const addItem = async (itemData) => {
    try {
      setError('');
      
      const response = await fetch(`${API_URL}/items`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(itemData),
      });
      
      const data = await response.json();
      
      if (data.success) {
        // Refresh the items list and stats
        fetchItems(searchTerm);
        fetchStats();
        return { success: true };
      } else {
        setError(data.error || 'Failed to add item');
        return { success: false, error: data.error };
      }
    } catch (err) {
      console.error('Error adding item:', err);
      setError('Failed to add item. Please try again.');
      return { success: false, error: 'Connection error' };
    }
  };

  // ========== UPDATE AN EXISTING ITEM ==========
  const updateItem = async (id, itemData) => {
    try {
      setError('');
      
      const response = await fetch(`${API_URL}/items/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(itemData),
      });
      
      const data = await response.json();
      
      if (data.success) {
        // Refresh the items list and stats
        fetchItems(searchTerm);
        fetchStats();
        setEditingItem(null); // Close the edit form
        return { success: true };
      } else {
        setError(data.error || 'Failed to update item');
        return { success: false, error: data.error };
      }
    } catch (err) {
      console.error('Error updating item:', err);
      setError('Failed to update item. Please try again.');
      return { success: false, error: 'Connection error' };
    }
  };

  // ========== DELETE AN ITEM ==========
  const deleteItem = async (id) => {
    // Ask user to confirm before deleting
    if (!window.confirm('Are you sure you want to delete this item?')) {
      return;
    }
    
    try {
      setError('');
      
      const response = await fetch(`${API_URL}/items/${id}`, {
        method: 'DELETE',
      });
      
      const data = await response.json();
      
      if (data.success) {
        // Refresh the items list and stats
        fetchItems(searchTerm);
        fetchStats();
      } else {
        setError(data.error || 'Failed to delete item');
      }
    } catch (err) {
      console.error('Error deleting item:', err);
      setError('Failed to delete item. Please try again.');
    }
  };

  // ========== HANDLE SEARCH ==========
  const handleSearch = (term) => {
    setSearchTerm(term);
    fetchItems(term);
  };

  // ========== START EDITING AN ITEM ==========
  const startEdit = (item) => {
    setEditingItem(item);
  };

  // ========== CANCEL EDITING ==========
  const cancelEdit = () => {
    setEditingItem(null);
  };

  // ========== QUICK UPDATE QUANTITY ==========
  // This is for the +/- buttons on each item
  const updateQuantity = async (id, newQuantity) => {
    if (newQuantity < 0) {
      setError('Quantity cannot be negative');
      return;
    }
    await updateItem(id, { quantity: newQuantity });
  };

  // ========== RENDER THE UI ==========
  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <h1>üì¶ Inventory Manager</h1>
        <p>Simple Inventory Management System</p>
      </header>

      {/* Main content */}
      <main className="main-content">
        {/* Statistics Panel */}
        <Stats stats={stats} />

        {/* Error message (if any) */}
        {error && (
          <div className="error-message">
            ‚ö†Ô∏è {error}
            <button onClick={() => setError('')} className="close-btn">√ó</button>
          </div>
        )}

        {/* Add/Edit Item Form */}
        <section className="form-section">
          <h2>{editingItem ? '‚úèÔ∏è Edit Item' : '‚ûï Add New Item'}</h2>
          <ItemForm
            onSubmit={editingItem 
              ? (data) => updateItem(editingItem.id, data)
              : addItem
            }
            initialData={editingItem}
            onCancel={editingItem ? cancelEdit : null}
          />
        </section>

        {/* Search Bar */}
        <section className="search-section">
          <h2>üîç Search Items</h2>
          <SearchBar
            searchTerm={searchTerm}
            onSearch={handleSearch}
          />
        </section>

        {/* Items List */}
        <section className="list-section">
          <h2>üìã Inventory Items ({items.length})</h2>
          {loading ? (
            <p className="loading">Loading items...</p>
          ) : (
            <ItemList
              items={items}
              onDelete={deleteItem}
              onEdit={startEdit}
              onUpdateQuantity={updateQuantity}
            />
          )}
        </section>
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>Simple Inventory Management System | Built with React & Node.js</p>
      </footer>
    </div>
  );
}

// Export the component so it can be used elsewhere
export default App;
