// ============================================================
// ITEMFORM.JS - Form Component for Adding/Editing Items
// ============================================================
// This component displays a form with fields for:
//   - Item name
//   - Quantity
//   - Price per unit
// It handles both adding new items and editing existing ones.
// ============================================================

import React, { useState, useEffect } from 'react';

// Props (inputs) this component receives:
// - onSubmit: function to call when form is submitted
// - initialData: existing item data (when editing)
// - onCancel: function to call when canceling an edit
function ItemForm({ onSubmit, initialData, onCancel }) {
  // ========== STATE FOR FORM FIELDS ==========
  // We use separate state variables for each form field
  
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formError, setFormError] = useState('');

  // ========== POPULATE FORM WHEN EDITING ==========
  // useEffect runs when initialData changes
  // It fills the form with existing item data when editing
  useEffect(() => {
    if (initialData) {
      setName(initialData.name || '');
      setQuantity(initialData.quantity?.toString() || '');
      setPrice(initialData.price?.toString() || '');
    } else {
      // Clear form when not editing
      setName('');
      setQuantity('');
      setPrice('');
    }
  }, [initialData]);

  // ========== FORM VALIDATION ==========
  // Check that all required fields are filled and valid
  const validateForm = () => {
    // Check name is not empty
    if (!name.trim()) {
      setFormError('Please enter an item name');
      return false;
    }

    // Check price is a valid positive number
    const priceNum = parseFloat(price);
    if (isNaN(priceNum) || priceNum < 0) {
      setFormError('Please enter a valid price (0 or more)');
      return false;
    }

    // Check quantity is a valid non-negative number (if provided)
    if (quantity !== '') {
      const qtyNum = parseInt(quantity);
      if (isNaN(qtyNum) || qtyNum < 0) {
        setFormError('Quantity must be 0 or more');
        return false;
      }
    }

    // All validations passed
    setFormError('');
    return true;
  };

  // ========== HANDLE FORM SUBMISSION ==========
  const handleSubmit = async (e) => {
    // Prevent the default form submission (page reload)
    e.preventDefault();

    // Validate the form
    if (!validateForm()) {
      return;
    }

    // Disable the submit button while processing
    setIsSubmitting(true);

    // Prepare the item data
    const itemData = {
      name: name.trim(),
      quantity: quantity === '' ? 0 : parseInt(quantity),
      price: parseFloat(price)
    };

    // Call the onSubmit function passed from parent
    const result = await onSubmit(itemData);

    // Re-enable the submit button
    setIsSubmitting(false);

    // If successful, clear the form (only for new items)
    if (result.success && !initialData) {
      setName('');
      setQuantity('');
      setPrice('');
    }
  };

  // ========== RENDER THE FORM ==========
  return (
    <form className="item-form" onSubmit={handleSubmit}>
      {/* Show form error if any */}
      {formError && (
        <div className="error-message" style={{ width: '100%' }}>
          ⚠️ {formError}
        </div>
      )}

      {/* Item Name Field */}
      <div className="form-group">
        <label htmlFor="name">Item Name *</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter item name"
          required
        />
      </div>

      {/* Quantity Field */}
      <div className="form-group">
        <label htmlFor="quantity">Quantity</label>
        <input
          type="number"
          id="quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          placeholder="0"
          min="0"
          step="1"
        />
      </div>

      {/* Price Field */}
      <div className="form-group">
        <label htmlFor="price">Price per Unit ($) *</label>
        <input
          type="number"
          id="price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          placeholder="0.00"
          min="0"
          step="0.01"
          required
        />
      </div>

      {/* Form Buttons */}
      <div className="form-buttons">
        <button
          type="submit"
          className="btn btn-primary"
          disabled={isSubmitting}
        >
          {isSubmitting
            ? 'Saving...'
            : initialData
            ? 'Update Item'
            : 'Add Item'}
        </button>
        
        {/* Show Cancel button only when editing */}
        {onCancel && (
          <button
            type="button"
            className="btn btn-secondary"
            onClick={onCancel}
          >
            Cancel
          </button>
        )}
      </div>
    </form>
  );
}

export default ItemForm;
