// ============================================================
// SEARCHBAR.JS - Search Input Component
// ============================================================
// This component provides a search input field that allows
// users to filter inventory items by name.
// ============================================================

import React from 'react';

// Props (inputs) this component receives:
// - searchTerm: the current search text
// - onSearch: function to call when user searches
function SearchBar({ searchTerm, onSearch }) {
  // ========== HANDLE INPUT CHANGE ==========
  // This runs every time the user types in the search box
  const handleChange = (e) => {
    onSearch(e.target.value);
  };

  // ========== CLEAR SEARCH ==========
  // This clears the search and shows all items
  const handleClear = () => {
    onSearch('');
  };

  // ========== RENDER THE SEARCH BAR ==========
  return (
    <div className="search-bar">
      {/* Search Input Field */}
      <input
        type="text"
        value={searchTerm}
        onChange={handleChange}
        placeholder="Search items by name..."
        aria-label="Search items"
      />
      
      {/* Clear Button - only shown when there's a search term */}
      {searchTerm && (
        <button
          type="button"
          className="btn btn-secondary"
          onClick={handleClear}
        >
          Clear
        </button>
      )}
    </div>
  );
}

export default SearchBar;
